CREATE TABLE TaxonomyModel (_id INTEGER PRIMARY KEY AUTOINCREMENT,LOCAL_SITE_ID INTEGER,NAME TEXT,LABEL TEXT,DESCRIPTION TEXT,IS_HIERARCHICAL INTEGER,IS_PUBLIC INTEGER);
CREATE TABLE SitePluginModel (_id INTEGER PRIMARY KEY AUTOINCREMENT,LOCAL_SITE_ID INTEGER,NAME TEXT,DISPLAY_NAME TEXT,PLUGIN_URL TEXT,VERSION TEXT,SLUG TEXT,DESCRIPTION TEXT,AUTHOR_NAME TEXT,AUTHOR_URL TEXT,SETTINGS_URL TEXT,IS_ACTIVE INTEGER,IS_AUTO_UPDATE_ENABLED INTEGER,UNIQUE (SLUG, LOCAL_SITE_ID));
CREATE TABLE ThemeModel (_id INTEGER PRIMARY KEY AUTOINCREMENT,LOCAL_SITE_ID INTEGER,THEME_ID TEXT,NAME TEXT,DESCRIPTION TEXT,SLUG TEXT,VERSION TEXT,AUTHOR_NAME TEXT,AUTHOR_URL TEXT,THEME_URL TEXT,SCREENSHOT_URL TEXT,DEMO_URL TEXT,DOWNLOAD_URL TEXT,STYLESHEET TEXT,PRICE_TEXT TEXT,FREE INTEGER,MOBILE_FRIENDLY_CATEGORY_SLUG TEXT,ACTIVE INTEGER,AUTO_UPDATE INTEGER,AUTO_UPDATE_TRANSLATION INTEGER,IS_WP_COM_THEME INTEGER);
CREATE TABLE PostModel (_id INTEGER PRIMARY KEY AUTOINCREMENT,LOCAL_SITE_ID INTEGER,REMOTE_SITE_ID INTEGER,REMOTE_POST_ID INTEGER,TITLE TEXT,CONTENT TEXT,DATE_CREATED TEXT,CATEGORY_IDS TEXT,CUSTOM_FIELDS TEXT,LINK TEXT,EXCERPT TEXT,TAG_NAMES TEXT,STATUS TEXT,PASSWORD TEXT,FEATURED_IMAGE_ID INTEGER,POST_FORMAT TEXT,SLUG TEXT,LATITUDE REAL,LONGITUDE REAL,IS_PAGE INTEGER,PARENT_ID INTEGER,PARENT_TITLE TEXT,IS_LOCAL_DRAFT INTEGER,IS_LOCALLY_CHANGED INTEGER,DATE_LOCALLY_CHANGED TEXT,LAST_KNOWN_REMOTE_FEATURED_IMAGE_ID INTEGER,HAS_CAPABILITY_PUBLISH_POST INTEGER,HAS_CAPABILITY_EDIT_POST INTEGER,HAS_CAPABILITY_DELETE_POST INTEGER);
CREATE TABLE MediaUploadModel (_id INTEGER PRIMARY KEY,UPLOAD_STATE INTEGER,PROGRESS REAL,ERROR_TYPE TEXT,ERROR_MESSAGE TEXT,FOREIGN KEY(_id) REFERENCES MediaModel(_id) ON DELETE CASCADE);
CREATE TABLE PostUploadModel (_id INTEGER PRIMARY KEY,UPLOAD_STATE INTEGER,ASSOCIATED_MEDIA_IDS TEXT,ERROR_TYPE TEXT,ERROR_MESSAGE TEXT,FOREIGN KEY(_id) REFERENCES PostModel(_id) ON DELETE CASCADE);
CREATE TABLE SubscriptionModel (_id INTEGER PRIMARY KEY AUTOINCREMENT,SUBSCRIPTION_ID TEXT,BLOG_ID TEXT,BLOG_NAME TEXT,FEED_ID TEXT,URL TEXT,SHOULD_NOTIFY_POSTS INTEGER,SHOULD_EMAIL_POSTS INTEGER,EMAIL_POSTS_FREQUENCY TEXT,SHOULD_EMAIL_COMMENTS INTEGER,UNIQUE (FEED_ID, URL));
CREATE TABLE AccountModel (_id INTEGER PRIMARY KEY,USER_NAME TEXT,USER_ID INTEGER,DISPLAY_NAME TEXT,PROFILE_URL TEXT,AVATAR_URL TEXT,PRIMARY_SITE_ID INTEGER,EMAIL_VERIFIED INTEGER,SITE_COUNT INTEGER,VISIBLE_SITE_COUNT INTEGER,EMAIL TEXT,HAS_UNSEEN_NOTES INTEGER,FIRST_NAME TEXT,LAST_NAME TEXT,ABOUT_ME TEXT,DATE TEXT,NEW_EMAIL TEXT,PENDING_EMAIL_CHANGE INTEGER,WEB_ADDRESS TEXT,TRACKS_OPT_OUT INTEGER);
CREATE TABLE PostFormatModel (SITE_ID INTEGER,SLUG TEXT,DISPLAY_NAME TEXT,_id INTEGER PRIMARY KEY AUTOINCREMENT);
CREATE TABLE RoleModel (_id INTEGER PRIMARY KEY AUTOINCREMENT,SITE_ID INTEGER,NAME TEXT,DISPLAY_NAME TEXT);
CREATE TABLE TermModel (_id INTEGER PRIMARY KEY AUTOINCREMENT,LOCAL_SITE_ID INTEGER,REMOTE_TERM_ID INTEGER,TAXONOMY TEXT,NAME TEXT,SLUG TEXT,DESCRIPTION TEXT,PARENT_REMOTE_ID INTEGER,POST_COUNT INTEGER);
CREATE TABLE SiteModel (_id INTEGER PRIMARY KEY AUTOINCREMENT,SITE_ID INTEGER,URL TEXT,ADMIN_URL TEXT,LOGIN_URL TEXT,NAME TEXT,DESCRIPTION TEXT,IS_WPCOM INTEGER,IS_FEATURED_IMAGE_SUPPORTED INTEGER,DEFAULT_COMMENT_STATUS TEXT,TIMEZONE TEXT,FRAME_NONCE TEXT,MAX_UPLOAD_SIZE INTEGER,MEMORY_LIMIT INTEGER,ORIGIN INTEGER,SELF_HOSTED_SITE_ID INTEGER,USERNAME TEXT,PASSWORD TEXT,XMLRPC_URL TEXT,SOFTWARE_VERSION TEXT,IS_SELF_HOSTED_ADMIN INTEGER,EMAIL TEXT,DISPLAY_NAME TEXT,IS_JETPACK_INSTALLED INTEGER,IS_JETPACK_CONNECTED INTEGER,JETPACK_VERSION TEXT,IS_AUTOMATED_TRANSFER INTEGER,IS_WP_COM_STORE INTEGER,HAS_WOO_COMMERCE INTEGER,IS_VISIBLE INTEGER,IS_PRIVATE INTEGER,IS_VIDEO_PRESS_SUPPORTED INTEGER,PLAN_ID INTEGER,PLAN_SHORT_NAME TEXT,ICON_URL TEXT,HAS_FREE_PLAN INTEGER,UNMAPPED_URL TEXT,HAS_CAPABILITY_EDIT_PAGES INTEGER,HAS_CAPABILITY_EDIT_POSTS INTEGER,HAS_CAPABILITY_EDIT_OTHERS_POSTS INTEGER,HAS_CAPABILITY_EDIT_OTHERS_PAGES INTEGER,HAS_CAPABILITY_DELETE_POSTS INTEGER,HAS_CAPABILITY_DELETE_OTHERS_POSTS INTEGER,HAS_CAPABILITY_EDIT_THEME_OPTIONS INTEGER,HAS_CAPABILITY_EDIT_USERS INTEGER,HAS_CAPABILITY_LIST_USERS INTEGER,HAS_CAPABILITY_MANAGE_CATEGORIES INTEGER,HAS_CAPABILITY_MANAGE_OPTIONS INTEGER,HAS_CAPABILITY_ACTIVATE_WORDADS INTEGER,HAS_CAPABILITY_PROMOTE_USERS INTEGER,HAS_CAPABILITY_PUBLISH_POSTS INTEGER,HAS_CAPABILITY_UPLOAD_FILES INTEGER,HAS_CAPABILITY_DELETE_USER INTEGER,HAS_CAPABILITY_REMOVE_USERS INTEGER,HAS_CAPABILITY_VIEW_STATS INTEGER,SPACE_AVAILABLE INTEGER,SPACE_ALLOWED INTEGER,SPACE_USED INTEGER,SPACE_PERCENT_USED REAL,UNIQUE (SITE_ID, URL));
CREATE TABLE PluginDirectoryModel (_id INTEGER PRIMARY KEY AUTOINCREMENT,SLUG TEXT,DIRECTORY_TYPE TEXT,PAGE INTEGER);
CREATE TABLE RewindStatus (_id INTEGER PRIMARY KEY AUTOINCREMENT,LOCAL_SITE_ID INTEGER,REMOTE_SITE_ID INTEGER,STATE TEXT NOT NULL,LAST_UPDATED INTEGER,REASON TEXT,CAN_AUTOCONFIGURE INTEGER,REWIND_ID TEXT,RESTORE_ID INTEGER,REWIND_STATUS TEXT,REWIND_PROGRESS INTEGER,REWIND_REASON TEXT);
CREATE TABLE ActivityLog (_id INTEGER PRIMARY KEY AUTOINCREMENT,LOCAL_SITE_ID INTEGER,REMOTE_SITE_ID INTEGER,ACTIVITY_ID TEXT NOT NULL,SUMMARY TEXT NOT NULL,TEXT TEXT NOT NULL,NAME TEXT,TYPE TEXT,GRIDICON TEXT,STATUS TEXT,REWINDABLE INTEGER,REWIND_ID TEXT,PUBLISHED INTEGER,DISCARDED INTEGER,DISPLAY_NAME TEXT,ACTOR_TYPE TEXT,WPCOM_USER_ID INTEGER,AVATAR_URL TEXT,ROLE TEXT);
