CREATE TABLE HTTPAuthModel (_id INTEGER PRIMARY KEY AUTOINCREMENT,ROOT_URL TEXT,REALM TEXT,USERNAME TEXT,PASSWORD TEXT,UNIQUE (ROOT_URL));
CREATE TABLE QuickStartModel (SITE_ID INTEGER,TASK_NAME TEXT,IS_DONE INTEGER,IS_SHOWN INTEGER,_id INTEGER PRIMARY KEY AUTOINCREMENT);
CREATE TABLE MediaModel (_id INTEGER PRIMARY KEY AUTOINCREMENT,LOCAL_SITE_ID INTEGER,LOCAL_POST_ID INTEGER,MEDIA_ID INTEGER,POST_ID INTEGER,AUTHOR_ID INTEGER,GUID TEXT,UPLOAD_DATE TEXT,URL TEXT,THUMBNAIL_URL TEXT,FILE_NAME TEXT,FILE_PATH TEXT,FILE_EXTENSION TEXT,MIME_TYPE TEXT,TITLE TEXT,CAPTION TEXT,DESCRIPTION TEXT,ALT TEXT,WIDTH INTEGER,HEIGHT INTEGER,LENGTH INTEGER,VIDEO_PRESS_GUID TEXT,VIDEO_PRESS_PROCESSING_DONE INTEGER,UPLOAD_STATE TEXT,FILE_URL_MEDIUM_SIZE TEXT,FILE_URL_MEDIUM_LARGE_SIZE TEXT,FILE_URL_LARGE_SIZE TEXT,HORIZONTAL_ALIGNMENT INTEGER,VERTICAL_ALIGNMENT INTEGER,FEATURED INTEGER,FEATURED_IN_POST INTEGER);
